import React from 'react';
import './App.css';
import SearchResults from '../SearchResults/SearchResults.js';
import SearchBar from '../SearchBar/SearchBar.js';
import Playlist from '../Playlist/Playlist.js';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      searchResults:[{ name: "name1",
        artist: "artist1",
        album: "album1",
        id: "id1",
        }],
      playlistName:"--change your playlist name--",
      playlistTracks:[{ name: "name2",
        artist: "artist2",
        album: "album2",
        id: "id2",
        }],
      //count:0,
    };
    this.addTrack=this.addTrack.bind(this);
    this.removeTrack=this.removeTrack.bind(this);
    this.updatePlaylistName=this.updatePlaylistName.bind(this);
  }

  addTrack( track ){
    if( this.state.playlistTracks.find( savedTrack => { return savedTrack.id === track.id } ) ) {
      return;
    } else {
      this.setState( prevState => {
        return {playlistTracks: prevState.playlistTracks.concat(track)};
      });
      /*
      this.setState( prevState => {
        return { count: prevState.count+1 };
      });*/

    }
  }

  removeTrack( track ){
    let tracks = this.state.playlistTracks;
    tracks = tracks.filter( currentTrack => { return currentTrack.id !== track.id });
    this.setState({
      playlistTracks:tracks
    });
  }

  updatePlaylistName( name ){
    this.setState({
      playlistName: name
    });
  }

  render(){
    return (
      <div>
        {/* DEBUGGER */}
        <h1>Ja<span className="highlight">mmm</span>ing {/*this.state.count*/}</h1>
        <div className="App">
          <SearchBar />
          <div className="App-playlist">
            <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}/>
            <Playlist
            playlistTracks={this.state.playlistTracks} 
            playlistName={this.state.playlistName}
            onRemove={this.removeTrack}/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
