import React from 'react';
import './App.css';
import SearchResults from '../SearchResults/SearchResults.js';
import SearchBar from '../SearchBar/SearchBar.js';
import Playlist from '../Playlist/Playlist.js';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state ={
      searchResults:[{ name: "name1",
        artist: "artist1",
        album: "album1",
        id: "id1",
      }],
      playlistName:null,
      playlistTracks:[{ name: "name1",
        artist: "artist1",
        album: "album1",
        id: "id1",
      }],
      count:0
    };
    this.addTrack=this.addTrack.bind(this);
  }

  addTrack( track ){
    this.setState( prevState => {
      return { count: prevState.count+1 };
    });
    if( this.state.playlistTracks.find( savedTrack => { return savedTrack.id === track.id } ) ) {
      return;
    } else {
      this.setState( prevState => {
        return {playlistTracks: prevState.playlistTracks.concat(track)};
      });

    }
  }

  removeTrack( track ){
  }

  render(){
    return (
      <div>
        <h1>Ja<span className="highlight">mmm</span>ing {this.state.count}</h1>
        <div className="App">
          <SearchBar />
          <div className="App-playlist">
            <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}/>
            <Playlist playlistTracks={this.state.playlistTracks} playlistName={this.state.playlistName}/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
